# modulate4Ask# Author  Karlis Edgars Ruza# Bakalaura Darbs # 2022function retval = modulateBpsk(data, frequency, fs)  # Calculate amount of samples in one period  periodLength = 1 / frequency;  samplesPerPeriod = (periodLength / (1 / fs));    # Define carrier  t = 0 : 1 / fs : periodLength;  carrierSin = sin(2 * pi * frequency * t);  carrierCos = sin(2 * pi * frequency * t + pi/2);      for i = 1 : length(data)    waveSample = mod(i, samplesPerPeriod);    if(waveSample == 0)      waveSample = samplesPerPeriod;;    endif      if (data(i) == 0)      modulatedSignal(i) = carrierSin(waveSample);    else      modulatedSignal(i) = carrierCos(waveSample);    endif  endfor            retval = modulatedSignal;endfunction